function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    setTimeout(function() {
        var kaution1 = g_form.getValue('car_id.kaution');
        var preisProTag1 = g_form.getValue('car_id.preis_pro_tag');

        //console.log("Die Summe beträgt: " + summe);
        if ((g_form.getValue('typ') == 'vorlesung') || (g_form.getValue('typ') == 'praktikum') || (g_form.getValue('typ') == 'uebung') ||
            (g_form.getValue('typ') == 'labor')) {

            g_form.setValue('prio', 2);
        } else {
            g_form.setValue('prio', 1);
        }


    }, 1000);






    //Type appropriate comment here, and begin script below

}
